mapper.select:
SELECT transactionNum, bidNum, buyerId AS declCom, sellerId AS blackPerson
FROM transaction
WHERE auctionNum = #{auctionNum}

mapper.insert:
INSERT INTO blacklist (
transactionNum, bidNum, declCom, blackPerson, title, content, roleType
) VALUES (
#{transactionNum}, #{bidNum}, #{declCom}, #{blackPerson}, #{title}, #{content},
#{roleType}
)
