mapper.update:
UPDATE auction
SET state = 'success'
WHERE endDate <= #{currentDate}
AND state = 'processing';

mapper.select:
SELECT a.auctionNum, a.id, a.title, a.startDate, a.endDate, a.state, a.startPrice, a.minSalary, a.maxSalary, a.bidMaxPrice, a.Occupation, a.period, a.education, a.employmentType, a.stack, a.Certification, a.location, a.portfolio, p.nickName, p.email, p.password, p.address, p.sex, p.age, p.personImage, p.status FROM auction a JOIN person p ON a.id = p.id WHERE 1=1
AND a.location IN
#{location}
AND a.stack IN
#{stack}
AND a.occupation IN
#{Occupation}
AND a.period IN
#{period}
AND a.education IN
#{education}
AND a.certification IN
#{Certification}
AND a.employmentType IN
#{employmentType}
ORDER BY a.auctionNum LIMIT #{offset}, #{pageSize}

mapper.update:
update auction set
bidMaxPrice=#{bidMaxPrice}
where auctionNum=#{auctionNum}

mapper.select:
select a.* , p.* from auction a JOIN person p ON a.id = p.id WHERE a.id = #{sellerId}
and STR_TO_DATE(a.endDate, '%Y-%m-%d') between STR_TO_DATE(#{filterDate}, '%Y-%m-%d') and STR_TO_DATE(now(), '%Y-%m-%d')
and STR_TO_DATE(a.endDate, '%Y-%m-%d') between STR_TO_DATE(#{startDate}, '%Y-%m-%d') and STR_TO_DATE(#{endDate}, '%Y-%m-%d')
ORDER BY case when a.state = 'processing' then 1 when a.state = 'unprocessing' then 2 ELSE 3 END, a.endDate DESC limit #{limit} offset #{offset};
